import { useState } from "react";

let empty = null;
const players = ["X", "O"];


export default function Game(){
  const [lastPlayer, setLastPlayer] = useState(0);
  const [history, setHistory] = useState([Array(9).fill(empty)])
  const currentSquares = history[currentMove]
  const [currentMove, setCurrentMove] = useState(0)

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares]
    setHistory([...history, nextSquares]);
    setLastPlayer((lastPlayer + 1 ) % 2);
  }

  function handleReset() {
    setHistory([Array(9).fill(empty)])
    setLastPlayer(0)
  }

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
    setLastPlayer(nextMove % 2);
  }

  const moves = history.map((_, move) => {
    let description;
    if (move > 0) {
      description = 'Go to move #' + move;
    } else {
      description = 'Go to game start';
    }
    return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );
  });



  return (
    <div className="game">
      <div className="game-board">
        <Board
          lastPlayer={lastPlayer} 
          squares={currentSquares}
          onPlay={handlePlay}
          onReset={handleReset}
          />
      </div>
      <div className="game-info">
        <ol>
          {moves}
        </ol>
      </div>


    </div>
  )
}


function Board({lastPlayer, squares, onPlay, onReset}) {
  // const [squares, setSquares] = useState(Array(9).fill(empty));


  function handleClick(idx) {
    if (squares[idx] != empty || calculateWinner(squares)){
      return;
    }

    const nextSquares = squares.slice();
    nextSquares[idx] = players[lastPlayer];
    onPlay(nextSquares);


  }

  const rows = [];
  let n = 0;

  for (let i=0; i < 3; i++) {
    const buttons = [];
    for (let j=0; j < 3; j++) {
      buttons.push(
        <Square key={n} 
        value={squares[n]} 
        // Somehow arrow function didn't work directly
        onSquareClick={(function (index){return () => handleClick(index)})(n)}
        />
      );
      n++;
    }  

    rows.push(
      <div key={i} className="board-row">
        {buttons}
      </div>
    );
  }

  const winner = calculateWinner(squares);
  let status;

  status = winner ? "Winner is " + winner: "Next player: " + players[lastPlayer]
  return <>
        <div className="status">{status}</div>
        {rows}
        <button onClick={onReset} className="reset-button">RESET</button>
        </>;

}


function Square({value, onSquareClick}) {

  return <button 
    className="square"
    onClick={onSquareClick}
    >
    {value}
    </button>;
}


function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
