export default function BetterBoard() {
  const rows = [];
  let buttonNumber = 1;

  for (let i = 0; i < 3; i++) {
    const buttons = [];
    for (let j = 0; j < 3; j++) {
      buttons.push(
        <button key={buttonNumber} className="square">
          X
        </button>
      );
      buttonNumber++;
    }
    rows.push(
      <div key={i} className="board-row">
        {buttons}
      </div>
    );
  }

  return <>{rows}</>;
}
