export default function Board() {
  const rows = [];
  let n = 1;

  for (let i=0; i < 3; i++) {
    const buttons = [];
    for (let j=0; j < 3; j++) {
      buttons.push(
        <Square n={n} />
      );
      n++;
    }


    rows.push(
      <div key={i} className="board-row">
        {buttons}
      </div>
    );
  }

return <>{rows}</>;

}

function Square({n}) {
  return <button className="square">{n}</button>;
}

