import { useState } from "react";

let empty = null;

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(empty));
  const [lastPlayer, setLastPlayer] = useState(0);

  function handleClick(idx) {
    if (squares[idx] != empty){
      return;
    }
    console.log('Lasy player: ', lastPlayer);
    const players = ["X", "O"];
    const nextSquares = squares.slice();
    nextSquares[idx] = players[lastPlayer];
    setSquares(nextSquares);
    setLastPlayer((lastPlayer + 1 ) % 2);

  }

  const rows = [];
  let n = 0;

  for (let i=0; i < 3; i++) {
    const buttons = [];
    for (let j=0; j < 3; j++) {
      buttons.push(
        <Square key={n} 
        value={squares[n]} 
        // Somehow arrow function didn't work directly
        onSquareClick={(function (index){return () => handleClick(index)})(n)}
        />
      );
      n++;
    }  

    rows.push(
      <div key={i} className="board-row">
        {buttons}
      </div>
    );
  }

  return <>{rows}</>;

}


function Square({value, onSquareClick}) {

  return <button 
    className="square"
    onClick={onSquareClick}
    >
    {value}
    </button>;
}
